export {plusEqual, minusEqual, divideEqual, multiplyEqual}

function plusEqual(thingOne, thingTwo) {
  thingOne += thingTwo
  return thingOne
}

function minusEqual(thingOne, thingTwo) {
  thingOne -= thingTwo
  return thingOne
}

function divideEqual(thingOne, thingTwo) {
  thingOne /= thingTwo
  return thingOne
}

function multiplyEqual(thingOne, thingTwo) {
  thingOne *= thingTwo
  return thingOne
}
